                                <input name="price" type="text" class="field" placeholder="Цена посылки" data-required="true">
                            </div>
                            <div class="col-md-6">
                                <textarea name="text" class="field field--area" placeholder="Описание посылки"></textarea>
                            </div>
                            <div class="col-md-6 text--nowrap">
                                <input id="checkbox" type="checkbox" class="field--checkbox" data-required="true">
                                <label for="checkbox" class="field field--checkbox__label flex-column flex-sm-row justify-content-center justify-content-sm-start align-items-start align-items-sm-center">
                                    Я согласен с&nbsp;<button class="link text--nowrap--mob" data-popup="popup" data-popupfor="address">Публичной офертой</button>
