                </form>
                <script>
                    function removeClassInp(e) {
                        $(e.target).removeClass('error');
                        $(e.target).off('keypress', removeClassInp);
                        $(e.target).off('change', removeClassInp);
                    }
                    function addClassInp(el) {
                        $(el).addClass('error');
                        $(el).on('keypress', removeClassInp);
                        $(el).on('change', removeClassInp);
                    }
                    $('form').submit(function() {
                        var texts = $('input[type="text"], input[type="number"]').filter(function() {
                            if ($(this).data('required')) {
                                if (!$(this).val()) { return addClassInp($(this)), true }
                                else { return false; }
                            }
                        });
                        var checkboxes = $('input[type="checkbox"]').filter(function() {
                            if ($(this).data('required')) {
                                if (!$(this).prop('checked')) { return addClassInp($(this)), true }
                                else { return false; }
                            }
                        });
                        return !texts.length && !checkboxes.length;
                    });
                    var countChars = $('input[name="price"]').val().length;
                    $('input[name="price"]').on('input', function() {
                        if (this.value.length >= 1) {
                            if (countChars < this.value.length) {
                                this.value = this.value.replace(/\$/g, '').replace(/^(.*?)$/gm,"$1\$");
                            } else {
                                this.value = this.value.replace(/\$/g, '').slice(0, -1).replace(/^(.*?)$/gm,"$1\$");
                            }
                        } else {
                            this.value = '';
                        }
                        countChars = this.value.length;
                    });
                </script>
            </div>
        </div>
    </div>
